    <style>
        :root {
            --primary-color: #223276;
            --secondary-color: #f8f9ff;
        }

        body {
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            background: linear-gradient(135deg, #f8f9ff 0%, #e4e7f5 100%);
            min-height: 100vh;
            display: flex;
            align-items: center;
            justify-content: center;
        }

        .login-container {
            width: 390px;
            padding: 30px 20px;
        }

        .logo {
            margin-bottom: 40px;
        }

        .logo img {
            height: 40px;
        }

        h1 {
            font-size: 32px;
            font-weight: 700;
            margin-bottom: 12px;
            color: #202020;
        }

        .subtitle {
            font-size: 14px;
            color: #555;
            margin-bottom: 30px;
            line-height: 1.5;
        }

        .title-underline {
            width: 30px;
            height: 3px;
            background-color: var(--primary-color);
            margin: 8px 0 15px 0;
        }

        .form-label {
            font-size: 14px;
            font-weight: 600;
            color: #333;
        }

        .form-control {
            border-radius: 25px;
            padding: 12px 20px;
            border: 1px solid #ddd;
            margin-bottom: 20px;
        }

        .input-icon-container {
            position: relative;
        }

        .input-icon {
            position: absolute;
            left: 15px;
            top: 15px;
            color: #777;
        }

        .input-with-icon {
            padding-left: 45px;
        }

        .password-visibility {
            position: absolute;
            right: 15px;
            top: 15px;
            color: #777;
            cursor: pointer;
        }

        .forgot-password {
            text-align: right;
            font-size: 14px;
            color: var(--primary-color);
            font-weight: 500;
            text-decoration: none;
        }

        .login-btn {
            background-color: var(--primary-color);
            border: none;
            border-radius: 25px;
            padding: 12px;
            font-weight: 600;
            margin-top: 10px;
            transition: background-color 0.2s;
        }

        .login-btn:hover {
            background-color: #1a2861;
        }

        .login-btn i {
            margin-left: 5px;
        }

        .terms {
            font-size: 12px;
            color: #666;
            text-align: center;
            margin-top: 20px;
        }

        .terms a {
            color: var(--primary-color);
            text-decoration: none;
            font-weight: 500;
        }

        .form-check-input {
            width: 18px;
            height: 18px;
            margin-top: 3px;
        }

        .form-check-label {
            font-size: 14px;
            color: #444;
            margin-left: 5px;
        }
    </style>
</head>

